// Convert format from new data to app format
export const quizSubjects: QuizSubject[] = Object.entries(questionData).map(([name, questions]) => ({
  name,
  questions: questions.map((q: any) => ({
    q: q.q,
    choices: q.choices,
    a: q.a,
    e: q.e
  }))
}));

export const choiceLabels = ["ก.", "ข.", "ค.", "ง."];

export function shuffleQuestions(questions: Question[]): Question[] {
  const shuffled = [...questions];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
}

// Convert from new format (a:'ก') to old format (answer: number)
export function convertAnswerToIndex(answerLetter: string): number {
  const index = choiceLabels.findIndex(label => label.startsWith(answerLetter));
  return index !== -1 ? index : 0;
}